import React from 'react'
import styled from 'styled-components'
import Link from 'next/link'
import logo from '../img/logo.png'
import BoxedButton from './boxedButton'
import { FaAngleDown } from 'react-icons/fa';

const Navbar = styled.div`
  width: auto;
  display: flex;
  justify-content: space-evenly;
  align-items: center;
  position: sticky;
  z-index: 300;
`;

const HoverLink = styled.div`
  width: 70px;
  overflow: hidden;
  height: 60px;
  text-align: center;
  display: block;
  color: #fff !important;
  font-size: 16px;
  text-transform: capitalize;
  font-weight: 400;
  display: inline-block;
  padding: 35px 10px;
  font-family: josefin sans,sans-serif;
  position: relative;
  cursor: pointer;
  &:hover {
    color: green;
    display: block
  }
  &:after {
    content: '';
    position: relative;
    display: block;
    bottom: 0px;
    width: 100%; 
    height: 2px;
    background: #78b454;
    margin-top: 30px;
    z-index: -1;
    -webkit-transform:scaleX(0);
    -ms-transform:scaleX(0);
    transform:scaleX(0);
    -webkit-transition: -webkit-transform .5s;
    transition:         transform .5s;
  }
  &:hover:after {
    position: relative;
    display: flex;
  -webkit-transform: scaleX(1);
      -ms-transform: scaleX(1);
          transform: scaleX(1);
  }
`;

export default function Nav() {
  return (
    <Navbar>
      <HoverLink><Link href='/about'>About</Link></HoverLink>
      <HoverLink><Link href='/about'>Servise</Link></HoverLink>
      <HoverLink><Link href='/about'>Menu</Link></HoverLink>
      <HoverLink><Link href='/about'>Gallery</Link></HoverLink>
      <Link href='/'><img src={logo} /></Link>
      <HoverLink><Link href='/about'>Blog </Link><FaAngleDown className="down-i"/></HoverLink>
      <HoverLink><Link href='/about'>Pages </Link><FaAngleDown className="down-i" /></HoverLink>
      <HoverLink><Link href='/about'>Contact</Link></HoverLink>
      <Link href="/"><BoxedButton>Costum Order</BoxedButton></Link>
    </Navbar>
  )
}
